// Handles the initation and manipulation of
// pixijs elements

import * as PIXI from "pixi.js"

class PIXIApp {

    private app: PIXI.Application; 

    constructor(w: number, h:number) {
        this.app = new PIXI.Application({height:h, width:w});
        var renderer = PIXI.autoDetectRenderer();document.body.appendChild(renderer.view);renderer.view.style.position = 'absolute';renderer.view.style.left = '50%';renderer.view.style.top = '50%';renderer.view.style.transform = 'translate3d( -50%, -50%, 0 )';
    }

}

export default PIXIApp;
