import * as PIXI from "pixi.js"

class PIXIApp {

    private app: PIXI.Application; 

    constructor(w: number, h:number) {
        const setup = () => {
            let sprite = new PIXI.Sprite(
                this.app.loader.resources["fetchlogo.jpg"].texture
              );
            this.app.stage.addChild(sprite);
        }
